    public SignoZodiaco getZodiaco(GregorianCalendar fecha) {
        int year = fecha.get(Calendar.YEAR);
        if (fecha.after(new GregorianCalendar(year-1, Calendar.DECEMBER, 21)) && fecha.before(new GregorianCalendar(year, Calendar.JANUARY, 19))) {
            return signos.get(2);
        }
        else if (fecha.after(new GregorianCalendar(year, Calendar.JANUARY, 20)) && fecha.before(new GregorianCalendar(year, Calendar.FEBRUARY, 18))) {
            return signos.get(0);
        }
        else if (fecha.after(new GregorianCalendar(year, Calendar.FEBRUARY, 19)) && fecha.before(new GregorianCalendar(year, Calendar.MARCH, 20))) {
            return signos.get(8);
        }
        else if (fecha.after(new GregorianCalendar(year, Calendar.MARCH, 21)) && fecha.before(new GregorianCalendar(year, Calendar.APRIL, 20))) {
            return signos.get(1);
        }
        else if (fecha.after(new GregorianCalendar(year, Calendar.APRIL, 21)) && fecha.before(new GregorianCalendar(year, Calendar.MAY, 20))) {
            return signos.get(10);
        }
        else if (fecha.after(new GregorianCalendar(year, Calendar.MAY, 21)) && fecha.before(new GregorianCalendar(year, Calendar.JUNE, 20))) {
            return signos.get(5);
        }
        else if (fecha.after(new GregorianCalendar(year, Calendar.JUNE, 21)) && fecha.before(new GregorianCalendar(year, Calendar.JULY, 20))) {
            return signos.get(3);
        }
        else if (fecha.after(new GregorianCalendar(year, Calendar.JULY, 21)) && fecha.before(new GregorianCalendar(year, Calendar.AUGUST, 21))) {
            return signos.get(6);
        }
        else if (fecha.after(new GregorianCalendar(year, Calendar.AUGUST, 22)) && fecha.before(new GregorianCalendar(year, Calendar.SEPTEMBER, 22))) {
            return signos.get(11);
        }
        else if (fecha.after(new GregorianCalendar(year, Calendar.SEPTEMBER, 23)) && fecha.before(new GregorianCalendar(year, Calendar.OCTOBER, 22))) {
            return signos.get(7);
        }
        else if (fecha.after(new GregorianCalendar(year, Calendar.OCTOBER, 23)) && fecha.before(new GregorianCalendar(year, Calendar.NOVEMBER, 22))) {
            return signos.get(4);
        }
        else if (fecha.after(new GregorianCalendar(year, Calendar.NOVEMBER, 23)) && fecha.before(new GregorianCalendar(year, Calendar.DECEMBER, 20))) {
            return signos.get(9);
        }
        return signos.get(0);
    }
    
